<script>
import { mapActions, mapState, mapGetters } from "vuex";
import noUser from "../../assets/img/png/userfeed.png";
import LoadingChallenge from "@/components/LoadingChallenge.vue";
import { onFile } from "../../stub/functions";
import ReportModal from "./ReportModal.vue";
export default {
  components: {

    LoadingChallenge,
  },
  props: {
    item: Object,
    index: Number,
    array: Array,
    idUser: Object,
    from: String,
  },
  data() {
    return {
      comment_old: this.item.comments.length,
      comment_new: null,
      activeLikeCount: null,
      showReport: false,
      idLike: null,
      addLikeCount: null,
      isLike: null,
      noUser,
      progress: 0,
      modalDownload: false,
      loading: false,
      active: false,
      activeComunitie: false,
      countDown: null,
      completeAnimation: null,
      completeChallenge: null,
      userId: null,
      idComents: null,
      fileTo: null
    };
  },
  computed: {
    urlBucket() {
      return process.env.api_assets_url;
    },
    ...mapState("createUserChallenge", ["progressBar"]),
    ...mapGetters("user", ["getUserData", "getAccountComents"]),
    ...mapActions("challenges", ["promotionalAction"]),
  },
  watch: {
    getAccountComents(value, oldValue) {
      this.comment_new = value

    },
    countDown(value) {
      console.log('CONTADO ANIMATION ==>', value)
      this.completeAnimation = value
      if (this.completeChallenge === 'from generic' && value <= 0) {
        if (localStorage.getItem("recordTwilio")) {
          localStorage.removeItem("recordTwilio")
        }
        if (this.from == 'communities') {
          this.$router.push({ path: `/mycommunities`, query: { tab: "communities" } })

        } else {
          this.$router.push({ path: `/challenges/${this.item.challenge.id}/communities`, query: { shared: true, u: this.idUser.id, id: this.challangeRes.id, donate: true } })
        }


        this.active = false;

      }
      if (this.completeChallenge === 'from promotional' && value <= 0) {
        if (localStorage.getItem("recordTwilio")) {
          localStorage.removeItem("recordTwilio")
        }
        if (this.from == 'communities') {
          this.$router.push({ path: `/mycommunities`, query: { tab: "communities" } })
        } else {
          this.$router.push({ path: `/challenges/${this.item.id}/communities`, query: { shared: true, u: this.idUser.id, id: this.challangeRes.id, donate: true } })
        }


        this.active = false;

      }
    },
    completeChallenge(value) {
      console.log('Subida Challenge ==>', value)
      this.completeChallenge = value
      if (value === 'from generic' && this.completeAnimation <= 0) {
        if (localStorage.getItem("recordTwilio")) {
          localStorage.removeItem("recordTwilio")
        }
        if (this.from == 'communities') {
          this.$router.push({ path: `/mycommunities`, query: { tab: "communities" } })
        } else {
          this.$router.push({ path: `/challenges/${this.item.challenge.id}/communities`, query: { shared: true, u: this.idUser.id, id: this.challangeRes.id, donate: true } })
        }


        this.active = false;

      }
      if (value === 'from promotional' && this.completeAnimation <= 0) {

        if (localStorage.getItem("recordTwilio")) {
          localStorage.removeItem("recordTwilio")
        }
        if (this.from == 'communities') {
          this.$router.push({ path: `/challenges/${this.item.id}/communities` })
        } else {
          this.$router.push({ path: `/challenges/${this.item.id}/communities`, query: { shared: true, u: this.idUser.id, id: this.challangeRes.id, donate: true } })
        }


        this.active = false;

      }
    }
  },
  methods: {
    ...mapActions("createUserChallenge", ["createUserChallengeAction"]),
    ...mapActions("user", ["nicknameAction", "sumaComments"]),
    openComments(item) {
      this.idComents = item.id
      this.$nuxt.$emit("openModalComments", {
        status: true,
        data: item,
        user: this.idUser,
      });
    },
    openReport(item){
      console.log("ABRIENDO");
      this.$nuxt.$emit("openModalReport", {
        status: true,
        data: item,
        user: this.idUser,
      });
    },
    giveLike(item) {
      this.nicknameAction().then((user) => {
        if (user) {
          this.activeLikeCount = true;
          this.idLike = item.id;
          this.addLikeCount = item.likeCount;
          this.addLikeCount += 1;
          this.$axios
            .$post("/like/create", { idUsr: user.id, idBadUsr: item.id })
            .then((res) => {
              this.dataLike = res;
              if (this.dataLike !== "") {
                this.isLike = true;
              }
            })
            .catch((err) => console.log(" catch", err));
        } else {
          this.$nuxt.$emit("fullScreenDefinitive", { status: false });
          this.$nuxt.$emit("openLogin", [true]);
        }
      });
    },
    communities(value) {
      this.$emit("communities", value);

    },
    openShareOptions(item) {
      this.$nuxt.$emit("openModalShareFriends", [
        item,
        true,
        this.from,
        this.idUser,
      ]);
    },
    getDownload(item) {
      this.loading = true;
      this.$axios({
        url: "/media/" + item.url,
        method: "GET",
      }).then((response) => {
        console.log("oja", response.data);
        const nameUrl = item.url.split("/");
        const blob = this.b64toBlob(response.data.data, response.data.type);
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", nameUrl[1]);
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        this.loading = false;
      });
    },
    b64toBlob(b64Data, contentType = "", sliceSize = 512) {
      const byteCharacters = atob(b64Data);
      const byteArrays = [];
      for (
        let offset = 0;
        offset < byteCharacters.length;
        offset += sliceSize
      ) {
        const slice = byteCharacters.slice(offset, offset + sliceSize);
        const byteNumbers = new Array(slice.length);
        for (let i = 0; i < slice.length; i++) {
          byteNumbers[i] = slice.charCodeAt(i);
        }
        const byteArray = new Uint8Array(byteNumbers);
        byteArrays.push(byteArray);
      }
      const blob = new Blob(byteArrays, { type: contentType });
      return blob;
    },
    infoCommnities(info) {
      console.log(' this.members,', this.postsImpact)
      this.$nuxt.$emit("openModalInfoCommunities", [
        true,
        info,
        this.members,
        "closetApp",
      ]);
    },
    async countDownTimer() {
      if (this.countDown > 0) {
        setTimeout(() => {
          this.countDown -= 1;
          this.countDownTimer();
        }, 1000);
        return "se termino";
      }
    },
    async onFileSelect(e, item) {
      console.log('e , item', e, item)
      this.loading = true;
      let getAnimation = null;
      this.promotionalAction.then(async (r) => {

        r.map((animation, index) => {
          if (item.challenge.id == animation.id) {
            getAnimation = animation
          }
          return getAnimation
        })
        console.log('getAnimation', getAnimation)
        if (getAnimation) {
          if (localStorage.getItem('recordTwilio')) {
            this.$axios
              .$post(`/stats/record`, { id: localStorage.getItem("recordTwilio"), step: "TWILIO_UPLOAD" })
              .then((resUpload) => {
                console.log('Twilio Upload', resUpload)
              })
          }

          this.progress = 0
          var sizeInMB = (e.target.files[0].size / (1024 * 1024)).toFixed(2);
          this.fileTo = sizeInMB
          let file = e.target.files[0];
          this.countDown = getAnimation?.sponsors[0]?.animation?.duration / 1000
          console.log('ANIMATION ====>', this.countDown)
          if (file.type == 'video/mp4' || file.type == 'video/MOV' || file.type == 'video/3GP' || file.type == 'video/AVI' || file.type == 'video/quicktime') {
            this.$emit('evetPause', this.index);
            if (this.idUser) this.active = true
            this.loading = false;
          }
          if (file.type == "image/jpg" || file.type == "image/jpeg" || file.type == "image/png" || file.type == "image/gif") {
            if (this.idUser) this.active = true
            this.loading = false;
          }
          console.log('this.idUser', this.idUser)
          if (this.idUser.id) {
            this.countDownTimer()
          }
          let result = await onFile(this.idUser, e, item, this.createUserChallengeAction, this.from, this.isShared?.idShare)
          console.log('RES CREATE ===>', result)
          if (result?.data) {
            if (result?.data?.id) {
              this.challangeRes = result?.data
              this.completeChallenge = result?.fromRes
              if (!result?.fromRes) {
                this.$nuxt.$emit("modalDownload", [true, 'Unsupported file type', '', 'MP4 MOV 3GP AVI QUICKTIME']);
              }
            } else {
              alert(result?.data?.response?.data?.message)
              this.active = false;
              this.countDown = 0;
              result = null;
            }
          } else {
            if (result?.fromRes === 'noUserChallenge') {
              this.active = false
              this.$nuxt.$emit("FullScreenLoginChallenge", [true, file, item, '', this.isShared?.idShare])
            }
            if (result?.fromRes === 'noUser') {
              this.active = false
              this.$nuxt.$emit("FullScreenLoginChallenge", [true, file, item, 'noPromotional', this.isShared?.idShare]);
            }
          }
        }
      }).catch((error) => {
        this.active = false
      }

      )
    },
  },
   created() {
   console.log('this.item :>> ', this.item);
   }, 
};
</script>
